package com.example.recyclerviewjava;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentContainerView;
import com.example.recyclerviewjava.databinding.FragmentContinentBinding;

import java.util.ArrayList;
import java.util.List;

public class ContinentFragment extends Fragment {

    public static String url = "data:image/jpeg;base64,";
    private FragmentContinentBinding binding;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        binding = FragmentContinentBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        List<GeoItem> continents = new ArrayList<>();
        continents.add(new GeoItem("Africa", url, null));
        continents.add(new GeoItem("Asia", url, null));
        continents.add(new GeoItem("Europe", url, null));
        continents.add(new GeoItem("America", url, null));
        continents.add(new GeoItem("Australia", url, null));

        GeoAdapter adapter = new GeoAdapter(continents, item -> {
            CountryFragment countryFragment = CountryFragment.newInstance(item.getName());
            continents.clear();
            requireActivity()
                    .getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container_view, countryFragment)
                    .addToBackStack(null)
                    .commit();
        });

        binding.recyclerView.setAdapter(adapter);
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}
